import React from 'react';
import styled from 'styled-components';
import { NAV_HEIGHT, CONTENT_WIDTH } from '../../data';
import { useSelector } from 'react-redux';

interface Props {}
const UpperNav = styled.div`
  border: red solid 1px;
  height: ${NAV_HEIGHT};
  width: 100%;
  position: absolute;
  top: ${props => (props.topNavDisplay ? '0' : '-100%')};
`;

const Container = styled.div`
  height: 100%;
  width: 100%;
  max-width: ${CONTENT_WIDTH}; /*For margins*/
  margin: auto; /*For margins*/
  display: flex;
  align-items: center;
  justify-content: space-between;
  border: green solid 1px;
  box-sizing: border-box;
`;

interface RootState {
  topNavDisplay: boolean;
}
const selectTopNavDisplay = (state: RootState) => state.topNavDisplay;

const Nav: React.FC<Props> = () => {
  let topNavDisplay = useSelector(selectTopNavDisplay);
  return (
    <UpperNav displayNav={topNavDisplay}>
      <Container>
        <div>nav Bar</div>
        <div>nav Bar</div>
      </Container>
    </UpperNav>
  );
};

export default Nav;
