import React from 'react';
import styled, { StyledFunction } from 'styled-components';
import { NAV_HEIGHT, CONTENT_WIDTH } from '../../data';
import { useSelector } from 'react-redux';

interface YourProps {
  invalid: boolean;
}

const input: StyledFunction<YourProps & React.HTMLProps<HTMLInputElement>> =
  styled.input;

const Input = input`
  border: ${p => (p.invalid ? 'red' : 'blue')};
  `;
interface Props {}

const UpperNav = styled.div`
  border: red solid 1px;
  height: ${NAV_HEIGHT};
  width: 100%;
  position: absolute;
  top: ${props => (props.topNavDisplay ? '0' : '-100%')};
`;

const Container = styled.div`
  height: 100%;
  width: 100%;
  max-width: ${CONTENT_WIDTH}; /*For margins*/
  margin: auto; /*For margins*/
  display: flex;
  align-items: center;
  justify-content: space-between;
  border: green solid 1px;
  box-sizing: border-box;
`;

const Nav: React.FC<Props> = () => {
  interface RootState {
    topNavDisplay: boolean;
  }
  const selectTopNavDisplay = (state: RootState) => state.topNavDisplay;
  let topNavDisplay = useSelector(selectTopNavDisplay);
  return (
    <UpperNav topNavDisplay={topNavDisplay}>
      <Container>
        <div>nav Bar</div>
        <div>nav Bar</div>
      </Container>
    </UpperNav>
  );
};

export default Nav;
