};
const App: React.FC<Props> = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    window.onscroll = () => {
      toggleNav();
    };
  }, []);
  const toggleNav = () => {
    dispatch({ type: 'toggle-topNavDisplay' });
  };
  return (
    <BrowserRouter>
      <Button onClick={toggleNav}>toggle</Button>
      <Nav />
      <InnerContainer>
        <Switch>
          <Route exact path="/" component={Home} />
          <Route exact path="/messenger/:mid" component={Messenger} />
        </Switch>
      </InnerContainer>
    </BrowserRouter>
  );
};

export default App;
