import React from 'react';
import styled from 'styled-components';

interface Props {}

const Container = styled.div`
  border: 1px solid blue;
  width: 100%;
  height: 100%;
  box-sizing: border-box;
  overflow-y: scroll;
`;

const MessengesDisplay: React.FC<Props> = () => {
  return (
    <Container>
      <div>
        Bonjour, J’ai commencé à travailler pour une firme de consultation
        actuarielle après avoir obtenu un diplôme en Mathématiques et
        Statistiques. Maintenant, après avoir passé 2 ans à analyser des
        données, j’aimerais faire partie d’une équipe qui développe des
        applications de base de données. Récemment, j’ai complété un certificat
        de ‘Full-Stack Web-Development’ à Concordia. Le programme était une
        bonne opportunité d’apprendre en profondeur: JavaScript, HTML, CSS,
        React, Redux, Node.js, et Express. J’ai hâte d’apprendre plus sur DRW,
        et j’espère attendre de vous bientôt! Merci pour votre considération,
        Teresa Lacroix
      </div>
    </Container>
  );
};

export default MessengesDisplay;
