import React from 'react';
import styled from 'styled-components';
import { NAV_HEIGHT, CONTENT_WIDTH } from '../../data';
import { useSelector } from 'react-redux';

interface Props {}
interface StyledProps {
  topNavDisplay: boolean;
}
let array = [];
for (i = 0; i < 100; i++) {
  array.push(i);
}
const Container = styled.div<StyledProps>`
  background-color: lightblue;
  min-height: 100vh;
  width: 100%;
  max-width: ${CONTENT_WIDTH}; /*For margins*/
  padding-top: ${props =>
    props.topNavDisplay ? NAV_HEIGHT : '0px'}; /*For top Nav */
  transition: padding-top 0.4s ease-in-out;
  box-sizing: border-box;
`;
interface RootState {
  topNavDisplay: boolean;
}
const Home: React.FC<Props> = () => {
  let topNavDisplay = useSelector((state: RootState) => state.topNavDisplay);
  return <Container topNavDisplay={topNavDisplay}></Container>;
};

export default Home;
